message('Determine whether to run all subsequent scripts')
if (L_curdate != intnx('day', L_curdate, 0, 'e', daytype = 'w') %>% strftime('%Y%m%d')) {
	message('Current data date <',L_curdate,'> is not workday, skip the process.')
	q()
}
